int starsx[16];
int starsy[16];

void stars(const char *colorString) {

    unsigned char r1 = parsehexdigits(colorString);
    unsigned char g1 = parsehexdigits(colorString+2);
    unsigned char b1 = parsehexdigits(colorString+4);
        
    int head=0;
    
    while (1) {
        
        int x=starsx[head];
        int y=starsy[head];
        
        r[x][y]=0;
        g[x][y]=0;
        b[x][y]=0;
        
        
        x = random() % SIZE_X;
        y = random() % SIZE_Y;
        
        SETRGB( x ,y ,  r1, g1 , b1 );


        starsx[head] =x;
        starsy[head] =y;
        
        head++;        
        head &=15;
                
        sendOPCPixels();
        
    }
    
    
    
}



#define pdist(a, v, c, d) sqrt( ((float)a - c) * ( (float) a - c) + ( (float) v - d) * ( (float) v - d))

void plasma() {
    
    
   float xmid = SIZE_X/2.0;
   float ymid = SIZE_Y/2.0;
   
//   float radius = sqrt( (xmid*xmid) + (ymid*ymid));

   int loop = 0;
   
   while (1) {
         
       for( int x=0; x<SIZE_X; x++) {
          
           for(int y=0;y<SIZE_Y;y++) {
               
             float time = loop/2.0 ;  
             
             float value =                

                
                   sin( pdist(x + time, y, xmid , ymid ) / 8.0) 
                 + sin(pdist(x, y, SIZE_X/4 , SIZE_Y/4 ) / 8.0)
                 + sin(pdist(x, y + time / 7, SIZE_X * 0.75, SIZE_Y * 0.25) / 7.0)
                 + sin(pdist(x, y, 40.0, 10.0) / 8.0);
               
             int color = (int) ((4 + value) * 32);
             
             SETRGB( x, y, color, color * 2, 255 - color);
                                       
           }
       }
       
       sendOPCPixels();
       usleep(10000);
       
       loop++;
       
   }
       
    
}

