#define PI (3.141592653589793)

#define SIZE_X 59
#define SIZE_Y 26

#define ASPECT_RATIO (16.0/23.0)        // Width/heigh (these are the imperical measurements in mm)

#define PIN_COUNT 6

unsigned char r[SIZE_X][SIZE_Y];     // Buffer of RGB values
unsigned char g[SIZE_X][SIZE_Y];     // Buffer of RGB values
unsigned char b[SIZE_X][SIZE_Y];     // Buffer of RGB values

#define ROWS_PER_PIN (((SIZE_Y-1) / PIN_COUNT)+1)

#define OPC_BYTECOUNT (SIZE_X*(PIN_COUNT*ROWS_PER_PIN)*3)       // 3 bytes per pixel for RGB


#define OPC_HEADERSIZE 4 		// 4 bytes in opc header
#define OPC_BUFFERSIZE (OPC_HEADERSIZE + OPC_BYTECOUNT)

unsigned char opcbuffer[ OPC_BUFFERSIZE ];   //Convert the buffer to a single char for each R,G, & B 

void initopcheader() {
		
	opcbuffer[0]=0x00;		// Channel=0 
	opcbuffer[1]=0x00;		// Command=0	
	opcbuffer[2]=(OPC_BYTECOUNT>>8);		// len MSB
	opcbuffer[3]=(OPC_BYTECOUNT&0xff);	// len LSB
	
}

int sockfd;

void sendOPCPixels() {
	
	initopcheader();			// TODO: Only do this once per run

    // printf("start... pins=%d, rowsperpin=%d\r\n", PIN_COUNT , ROWS_PER_PIN);
	
    unsigned char *m = opcbuffer+OPC_HEADERSIZE;	
            
    for( int pin = 0; pin <PIN_COUNT ; pin++ ) {                // OPC packet has each string snet sequentially
        
        for( int row=0; row < ROWS_PER_PIN ; row++ ) {
                       
            int y= ( pin ) + ( row * PIN_COUNT );

           // printf("p,r,y=%d,%d,%d\r\n",pin,row,y);
                                   
            if (y>=SIZE_Y) {      // Of the screen?
                                
                for( int i=0;i<SIZE_X * 3;i++) {
                    
                    *(m++) = 0x00;                                        
                    // Send zeros as padding
                    
                }
                
            } else {                
                        
                if (row&1) {  // Alternating rows go back and forth
                                        
                    int x=SIZE_X;
            
                    while (x--) {
                                        
                        *(m++) = (r[x][y]) ;
                        *(m++) = (g[x][y]) ;                        
                        *(m++) = (b[x][y]) ;
                        
                    }
                    
                } else {
                    
                    for( int x=0; x<SIZE_X;x++) {

                        *(m++) = (r[x][y]) ;
                        *(m++) = (g[x][y]) ;
                        *(m++) = (b[x][y]) ;
                     
                    }                
               }                
            }
            
			
        }
    }
	
    int x=write( sockfd , opcbuffer ,  OPC_BUFFERSIZE );		// 3 bytes (r,g,b) per pixel	
    
}

#define SETRGB( x, y , red , green, blue ) {r[x][y]=red;g[x][y]=green;b[x][y]=blue;}
    

/**** Periodic timer stuff from http://www.2net.co.uk/tutorial/periodic_threads */


struct periodic_info
{
    int timer_fd;
    unsigned long long wakeups_missed;
};

static int make_periodic (unsigned int period, struct periodic_info *info)
{
    int ret;
    unsigned int ns;
    unsigned int sec;
    int fd;
    struct itimerspec itval;

    /* Create the timer */
    fd = timerfd_create (CLOCK_MONOTONIC, 0);
    info->wakeups_missed = 0;
    info->timer_fd = fd;
    if (fd == -1)
        return fd;

    /* Make the timer periodic */
    sec = period/1000000;
    ns = (period - (sec * 1000000)) * 1000;
    itval.it_interval.tv_sec = sec;
    itval.it_interval.tv_nsec = ns;
    itval.it_value.tv_sec = sec;
    itval.it_value.tv_nsec = ns;
    ret = timerfd_settime (fd, 0, &itval, NULL);
    return ret;
}

static void wait_period (struct periodic_info *info)
{
    unsigned long long missed;
    int ret;

    /* Wait for the next timer event. If we have missed any the
       number is written to "missed" */
    ret = read (info->timer_fd, &missed, sizeof (missed));
    if (ret == -1)
    {
        perror ("read timer");
        return;
    }

    /* "missed" should always be >= 1, but just to be sure, check it is not 0 anyway */
    if (missed > 0)
        info->wakeups_missed += (missed - 1);
}

static void end_periodic(struct periodic_info *info) {
    
    close( info->timer_fd);
}

/**** End periodic timer stuff */

    
void sinewaves(){
    
    for( int loop = 0; loop<1000;loop++) {
        
    
        for( int x=0; x<SIZE_X; x++) {
        
            for(int y=0;y<SIZE_Y;y++) {
            
                int color = (int) (0.5+ ( ( 100.0 + (100.0 * sin( (( (1.0* loop) / 1000) + ( (1.0 * x) / SIZE_X) ) * 3.1415 * 20 )  ) ) ) );
            
                SETRGB( x , y , color , color , color );
            
            }
        }
        
        sendOPCPixels();
//        usleep(100000);        
        
    }        
    
}    


unsigned char parsehexdigit( const char s) {
    
    if (isdigit(s)) return( s - '0' );
    if (islower(s) && s<='f') return( s - 'a' + 10);
    if (isupper(s) && s<='F') return( s - 'A' + 10);
    return(0);
}


unsigned char parsehexdigits( const char *s ) {
    
    return( ( parsehexdigit(*s) *16  ) + parsehexdigit( *(s+1)) );
    
}



#define BE_FRAMERATE    30          // Number of frames per second
#define BE_STEPS        100        // Steps per animation cycle
#define BE_RINGS        3           // Number of bullseye rings

#define COLOR_STEPS 100 			// USed for color cycle mode

void bullseyes( const char *colorString ){
    
    unsigned char r1 = parsehexdigits(colorString);
    unsigned char g1 = parsehexdigits(colorString+2);
    unsigned char b1 = parsehexdigits(colorString+4);    
    
    unsigned int colorstep = 0;		// For rainbow display
              
    float xmid = SIZE_X/2.0;
    float ymid = SIZE_Y/2.0;
    
    float radius = sqrt( (xmid*xmid) + (ymid*ymid));
    
    struct periodic_info pi;
    
    make_periodic( 1000000UL / BE_FRAMERATE , &pi );           // Animation Speed
    
    while (1) {
	    
	    if ( ! *colorString ) { 	// No color specified, so run a rainbow
	     
		  colorstep++;
		  
		  if (colorstep>=COLOR_STEPS) {
			  colorstep=0;
		  }

		// HSV to RGB
		  
		double      hh, p, q, t, ff;
		long        i;

		    hh =  (colorstep * 360.0 ) / COLOR_STEPS ;
		    
		    hh /= 60.0;
		    
		    i = (long)hh;
		    
		    ff = hh - i;
		    p = 1.0 * (1.0 - 1.0);
		    q = 1.0 * (1.0 - (1.0 * ff));
		    t = 1.0 * (1.0 - (1.0 * (1.0 - ff)));

		    switch(i) {
		    case 0:
			   r1 = 255;
			   g1 = t * 255;
			   b1 = p * 255;
			   break;
		    case 1:
			   r1 = q * 255;
			   g1 = 255;
			   b1 = p * 255;
			   break;
		    case 2:
			   r1 = p  * 255;
			   g1 = 255;
			   b1 = t * 255;
			   break;

		    case 3:
			   r1 = p * 255;
			   g1 = q * 255;
			   b1 = 255;
			   break;
		    case 4:
			   r1 = t * 255;
			   g1 = p *255;
			   b1 = 255;
			   break;
		    case 5:
		    default:
			   r1 = 255;
			   g1 = p * 255;
			   b1 = q *255;
			   break;
		    }		  
				  		  		  
		}
	  	    

        for( float step = 0; step < 2.0 * PI ; step += ( 2.0*PI / BE_STEPS )) {     // One full animation cycle
                
            for( int x=0; x<SIZE_X; x++) {
                
                float xd = (xmid - x ) * ASPECT_RATIO;		// Correct pitch aspect ratiox;

                float xsq = xd * xd;
                            
                for(int y=0;y<SIZE_Y;y++) {
                    
                    float yd = ymid - y; 
                    
                    float ysq = yd * yd;
                    
                    float distance = sqrt( ysq + xsq )/radius;
                    
                    // distance is normalized 0-1
                                    
                    float color =

                            (
                                sin(  (distance * 2 * PI * BE_RINGS) + step )  
                            
                             + 1 ) /2;      // normalized to 0-1
                    
                    //printf("x,y,d,c=%d,%d,%f,%f\r\n",x,y,distance,color);
				

				r[x][y]= (color*r1);
				g[x][y]= (color*g1);
				b[x][y]= (color*b1);
									
				
                }   
            }
		  

            
            wait_period(&pi);

            sendOPCPixels();

            if (pi.wakeups_missed) {
		
                fprintf(stderr,"Missed:%lu\r\n", (unsigned long) pi.wakeups_missed );
			 pi.wakeups_missed=0;
            
            }
            
        }      

    }

   end_periodic(&pi);

}



