void full( const char *colorString ) {
    
    unsigned char r1 = parsehexdigits(colorString);
    unsigned char g1 = parsehexdigits(colorString+2);
    unsigned char b1 = parsehexdigits(colorString+4);
        
    for( int x=0; x<SIZE_X; x++) {
            
        for(int y=0;y<SIZE_Y;y++) {
                
            r[x][y] = r1;
            g[x][y] = g1;
            b[x][y] = b1;
                            
                
        }
    }    
    
    struct periodic_info pi;
    
    make_periodic( 5000 , &pi );
    
    while(1) {
    

        sendOPCPixels();
        wait_period(&pi);
    }

    
}


void corners( const char *colorString ) {
    
    unsigned char r1 = parsehexdigits(colorString);
    unsigned char g1 = parsehexdigits(colorString+2);
    unsigned char b1 = parsehexdigits(colorString+4);
        
    typedef struct {
        unsigned int x;
        unsigned int y;
    } point_t;

    point_t points[] = { 
        {0,SIZE_Y-1} , 
        {SIZE_X-1, SIZE_Y-1},
        {0,0} , 
        {SIZE_X-1,0} ,          
    };


    
    struct periodic_info pi;
    
    make_periodic( 50000 , &pi );

    unsigned char flip=0;
    
    while(1) {

        for(int i=0; i<  sizeof( points) / sizeof( points[0]) ; i++ ) {

                unsigned int x=points[i].x;
                unsigned int y=points[i].y;

                if (flip) {

                    r[x][y] = 0;
                    g[x][y] = 0;
                    b[x][y] = 0;


                } else {

                    r[x][y] = r1;
                    g[x][y] = g1;
                    b[x][y] = b1;        

                }        
        }

        flip=!flip;
        

        sendOPCPixels();
        wait_period(&pi);
        
    }

    
}



void half( const char *colorString ) {
    
    unsigned char r1 = parsehexdigits(colorString);
    unsigned char g1 = parsehexdigits(colorString+2);
    unsigned char b1 = parsehexdigits(colorString+4);
    
    unsigned int marginx = (SIZE_X /4)+5 ;
        
    for( int x=marginx; x< (SIZE_X-marginx) ; x++) {
        
        for(int y=0;y<SIZE_Y;y++) {
            
            r[x][y] = r1;
            g[x][y] = g1;
            b[x][y] = b1;
            
            
        }
    }
    
    sendOPCPixels();
    
}



int opensocket() {

        int n;

    struct sockaddr_in servaddr;

        sockfd = socket(AF_INET, SOCK_DGRAM, 0);
        int broadcastEnable=1;
        int ret=setsockopt(sockfd, SOL_SOCKET, SO_BROADCAST, &broadcastEnable, sizeof(broadcastEnable));

/* Add other code, sockaddr, sendto() etc. */


//    sockfd=socket(AF_INET,SOCK_STREAM,0);
    bzero(&servaddr,sizeof servaddr);

    servaddr.sin_family=AF_INET;
    servaddr.sin_port=htons(7890);

    inet_pton(AF_INET,"192.168.1.255",&(servaddr.sin_addr));

    connect(sockfd,(struct sockaddr *)&servaddr,sizeof(servaddr));

    return(sockfd);

}


